import time

fn abs(x int) int {
	if x.str().contains("-") {
		return x * -1
	} else {
		return x
	}
}

fn sign(x int) int {
    if x > 0 {
        return 1
	}
    else if x < 0 {
        return -1
	}
    else { 
		return 0
	}
}

fn draw_line(x1 int, y1 int, x2 int, y2 int, mut board []string) {
  mut x := x1
  mut y := y1
  mut dx := abs(x2 - x1 )
  mut dy := abs(y2 - y1 )
  mut s1 := sign(x2 - x1)
  mut s2 := sign(y2 - y1)
	mut interchange := "false"

	if dy > dx {
    mut t := dx
    dx = dy
    dy = t
    interchange = "true"
	} else {
		interchange = "false"
	}

	mut error := 2*dy - dx
  mut a := 2*dy
	mut b := 2*dy - 2*dx

	mut index := (25 * (y -1)) + (x-1)
	board[index] = "\u001b[31m# \u001b[0m"
	println(board.join("") )

	mut i := -1
	for i < (dx - 1) {
		i++

    if error < 0 {
      if interchange != "false" {
        y = y + s2
			} else {
        x = x + s1
				error = error + a
			}
		} else {
      y = y + s2
      x = x + s1
      error = error + b
		} 

		index = (25 * (y -1)) + (x-1)
		board[index] = "\u001b[31m# \u001b[0m"
		println(board.join("") )
	}
}

