fn square_thick(scale int, mut board []string) {
	draw_line(3,3, 4 * scale,3, mut board) // front top
	draw_line(3,3, 3,4 * scale, mut board) // front left
	draw_line(4 * scale,3, 4 * scale,4 * scale, mut board) // front right
	draw_line(3,4 * scale, 4 * scale,4 * scale, mut board) // front bottom
}

fn square_inner(scale int, mut board []string) {
	draw_line(5,5,  6 * scale,5, mut board) // front top
	draw_line(5,5,  5,6 * scale, mut board) // front left
	draw_line(6 * scale,5,  6 * scale,6 * scale, mut board) // front right
	draw_line(5,6 * scale,  6 * scale,6 * scale, mut board) // front bottom
}

fn midlle_lines(scaleMain int, scaleSecond int, mut board []string) {
	draw_line(3,3,  5,5, mut board)
	draw_line(4 * scaleMain,3,  6 * scaleSecond,5, mut board)
	draw_line(3,4  * scaleMain,  5,6 * scaleSecond, mut board)
	draw_line(4 * scaleMain,4 * scaleMain,  6 * scaleSecond,6 * scaleSecond, mut board)

}

fn three_dimensional_square(scaleMain int, scaleSecond int, mut board []string) {
	square_thick(scaleMain, mut board)
	square_inner(scaleSecond, mut board)
	midlle_lines(scaleMain, scaleSecond, mut board)
}
